#!/usr/bin/python# -*- coding: UTF-8 -*-#  Python 函数(Python内置很多有用的函数)  ## abs() 绝对值函数a = 10print (abs(-a))# max() 取所有参数中的最大值b = max(2, 3, 52, -100, 6, -76, 9, -10)print (b)# 数据类型转换# Python 内置的函数中还包括数据类型转换函数# int() 字符串，float，double类型转换为整型c = '124'print (int(c))# float() 字符串，double转换成单精度浮点数d = '1243.45462233332'print (float(d))# Python 不支持double类型的浮点数# str() 数字转换字符串print (str(float(d)))# bool() 0，1转换为false，true 0和空字符串转换结果为false 其他为trueprint (bool('0'))